const CovidTrackerComponent = () => {
    const [chartData, setChartData] = useState({});
    
    const chart = () => {
        let population = 0, total_cases = 0, total_deaths = 0, total_recovered;
        let cumulative_dates = [], cumulative_cases = [], cumulative_deaths = [], cumulative_recovered = [];

        CovidTrackerService.getData()
        .then(res=>{
            const latest_data = res.data.latest_data;
            population = Number(res.data.population);
            total_deaths = Number(latest_data.deaths);
            total_cases = Number(latest_data.confirmed);
            total_recovered = Number(latest_data.recovered);
            for(const data of res.data.timeline.reverse()){
                cumulative_dates.push(data.date);
                cumulative_cases.push(Number(data.confirmed));
                cumulative_deaths.push(Number(data.deaths));
                cumulative_recovered.push(Number(data.recovered));
            }
            // Getting daily recent 7 days data
            //daily = timeline.slice(0,7);
            setChartData({
                labels: cumulative_deaths,
                datasets: [
                    {
                        label: "Confirmed",
                        data: cumulative_cases,
                        backgroundColor: ["rgba(75,192,192,0.6)"],
                        borderWidth: 4
                    },
                    {
                        label: "Deaths",
                        data: cumulative_deaths,
                        backgroundColor: ["rgba(255,192,192,0.6)"],
                        borderWidth: 4
                    }
                ]
            });

        }).catch(err=>console.log(err));
    };
    useEffect(()=>{chart()}, []);

    return (
        <Line data = {chartData}
            options = {{
                responsive: false,
                
                maintainAspectRatio: false, 
                title: {text: "Cumulative Graph", display: true},
                scales: {
                    yAxes: [
                        {
                            ticks: {
                                autoSkip: true,
                                maxTicksLimit: 10,
                                beginAtZero: true
                            },
                            gridLines: {
                                display: false
                            }
                        }
                    ],
                    x: [
                        {
                            gridLines: {
                                display: false
                            }
                        }
                    ]
                }
            }} />
    );

}


export default CovidTrackerComponent;
