import { UserContext } from "./UserContext";
import {useState, useContext, useEffect} from 'react';
import axios from "axios";
import './history.css';


//Bootstrap and jQuery libraries
import 'bootstrap/dist/css/bootstrap.min.css';
import 'jquery/dist/jquery.min.js';

//Datatable Modules
import "datatables.net-dt/js/dataTables.dataTables.js"
import "datatables.net-dt/css/jquery.dataTables.min.css"
import $ from 'jquery';


const PatientHistory = () => {
    const {user, setUser} = useContext(UserContext);
    const [history, setHistory] = useState(null);

    useEffect(() => {
        if (user) {
            axios.get(`http://localhost:8080/consultations/patient/${user.pid}`)
                .then(resp => {
                    const data = resp.data;
                    console.log(data);
                    setHistory(data);
                }).catch(err => {
                    console.log(err);
                })
        }
    
        $(document).ready(function () {
            setTimeout(function(){
            $('#hist').DataTable();
                } ,1000);
        });
    }, [])


    return (
        <div className = "container">
            <div className = "row">
                {
                    history && 
                        <div>
                            <table cellPadding="20px" className="table" id="hist">
                                <thead>
                                    <tr>
                                        <th colSpan="1" rowSpan="2"> Doctor</th>
                                        {/* <td colSpan="5">Consultations</td> */}
                                    </tr>
                                    <tr>
                                        <th>Id</th> <th>Date</th> <th>diagnosis</th> <th>medicines</th> <th>prognosis</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {    
                                        history.patient_consultations.map(c =>
                                            <tr> 
                                                {
                                                    c.consultations.map((cons,index) =>
                                                        <tr>
                                                            { index==0 ? <td rowSpan = {`${c.consultations.length}`}>
                                                                {c.doctor.name}
                                                            </td> : <td></td>}
                                                            <td>{cons.cid}</td><td>{cons.date}</td><td>{cons.diagnosis}</td><td>{cons.medicines}</td><td>{cons.prognosis}</td>
                                                        </tr>
                                                    )
                                                }
                                            </tr>    
                                        )
                                    }
                                </tbody>
                            </table>
                        </div>
                }
                
                
                
            </div>             
        </div>
    )
}

export default PatientHistory
