import axios from 'axios';
import React, {useState} from 'react'
import './login.css'
import Navbar from './Navbar'

function Login() {

    const [login,setLogin] = useState();
    
    const setData = (event) => {
        let data = event.target.value;
        let id = event.target.id;
        if (id === 'floatingPassword') {
            setLogin(prevData => ({...prevData, password: data}));
        } else if (id === 'floatingInput') {
            setLogin(prevData => ({...prevData, email: data}));
        }        
    }

    const handleSignIn = (e) => {
        e.preventDefault();

        axios.post('http://localhost:8080/login', login)
            .then(resp => {
                console.log(resp.data);
            }).catch(err => {
                console.log('Login failed');
            })

        

        //console.log(login);
    }


    return (
        <div class="container-fluid ps-md-0">
        <div class="row g-0">
          <div class="d-none d-md-flex col-md-4 col-lg-6 bg-image"></div>
          <div class="col-md-8 col-lg-6">
            <div class="login d-flex align-items-center py-5">
              <div class="container">
                <div class="row">
                  <div class="col-md-9 col-lg-8 mx-auto">
                    <h3 class="login-heading mb-4" >Welcome back!</h3>

                    <form>
                      <div class="form-floating mb-3">
                        <input type="email" class="form-control" id="floatingInput" placeholder="name@example.com" onChange={setData}/>
                        <label for="floatingInput">Email address</label>
                      </div>
                      <div class="form-floating mb-3">
                        <input type="password" class="form-control" id="floatingPassword" placeholder="Password" onChange={setData}/>
                        <label for="floatingPassword">Password</label>
                      </div>
                      <span id='error'></span>
                      <div class="d-grid">
                        <button class="btn btn-lg btn-primary btn-login text-uppercase fw-bold mb-2" type="submit" onClick={handleSignIn}>Sign in</button>
                        <div class="text-center">
                          <a class="small me-2" href="#">Forgot password?</a>
                          <a class="small" href="#">Register with us?</a>
                        </div>
                      </div>
      
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
}

export default Login
