import { UserContext } from "./UserContext";
import {useState, useContext, useEffect} from 'react';
import axios from "axios";

const PatientHistory = () => {
    const {user, setUser} = useContext(UserContext);
    const [history, setHistory] = useState(null);

    useEffect(() => {
        axios.get(`http://localhost:8080/consultations/patient/${user.pid}`)
            .then(resp => {
                const data = resp.data;
                console.log(data);
                setHistory(data);
            }).catch(err => {
                console.log(err);
            })
    }, [])


    return (
        <div className = "container">
            <div className = "row">
                {
                    history && 
                        <div>
                            <table>
                                <thead>
                                    <tr>
                                        <th>Patient ID</th> <th>Name</th> <th>Date of birth</th> <th>Address</th> <th>City</th> <th>Mobile</th> <th>Email</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    <tr>
                                        <td>{history.patient.pid}</td>
                                        <td>{history.patient.name}</td>
                                        <td>{history.patient.dob}</td>
                                        <td>{history.patient.address}</td>
                                        <td>{history.patient.city}</td>
                                        <td>{history.patient.mobile}</td>
                                        <td>{history.patient.email}</td>
                                    </tr>
                                </tbody>
                            </table>

                            <table border="2px solid red" cellSpacing="50px" cellPadding="50px">
                                <thead>
                                    <tr>
                                        <th colSpan="1" rowSpan="2">
                                            Doctor
                                        </th>
                                        <th colSpan="5">
                                            Consultations
                                        </th>
                                    </tr>
                                    <tr>
                                        <th>Id</th> <th>Date</th> <th>diagnosis</th> <th>medicines</th> <th>prognosis</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                        history.patient_consultations.map(c =>
                                            <tr>
                                                <td rowSpan = {`${c.consultations.length+1}`}>
                                                    {c.doctor.name}
                                                </td>
                                                {
                                                    c.consultations.map(cons=>
                                                        <tr>
                                                            <td>{cons.cid}</td><td>{cons.date}</td><td>{cons.diagnosis}</td><td>{cons.medicines}</td><td>{cons.prognosis}</td>
                                                        </tr>
                                                    )
                                                }
                                            </tr>
                                        )
                                    }
                                </tbody>
                            </table>
                        </div>
                }
                
                
                
            </div>             
        </div>
    )
}

export default PatientHistory
